            //     }}))
            //     console.log(todoList);

            // }

            // console.log(todoList);

            // todoArray.forEach(todo => {
            //     setTodolist(...todoList, {
            //         title: todo.title,
            //         descriptin: todo.description
            //     })
            // })

        } catch (err) {
            console.log(err);
        }
    }


    return (
        <div className='page'>
            <div className='container'>
                <h2>Get Todo's</h2>
                <button className='type-button' value='GetTodos' onClick={getTodos}>Fetch Todos</button>
            </div>
        </div>
    )
}
