import React, { useState, useContext, useEffect } from 'react'
import { useHistory, Link } from 'react-router-dom';
import UserContext from '../../context/UserContext';
import API from '../../utils/todoAPI';
import ErrorNotice from '../misc/ErrorNotice';

export default function CreateTodos() {

    const [title, setTitle] = useState('');
    const [youTubeUrl, setYouTubeUrl] = useState('');
    const [description, setDescription] = useState('');
    const [error, setError] = useState();

    const { userData } = useContext(UserContext);
    const history = useHistory();

    useEffect(() => {
        if (!userData.user) {
            history.push('/login')
        }
    });

    const submit = async (e) => {
        e.preventDefault();
        try {
            const newTodo = { title, youTubeUrl, description }

            console.log(userData.token)

            await API.createTodos(userData.token)
                .then(res => console.log('createtotos', res.data))
                .catch(err =>
                    (err.response.data.msg && setError(err.response.data.msg))
                )

        } catch (error) {

        }
    }
    // form - 1. title, 2. description, 3. youtube link,
    // 

    return (
        <div className='page'>
            <div className='container'>
                <h2>CreateTodos</h2>
                {error && (
                    <ErrorNotice message={error} clearError={() => setError(undefined)} />
                    )}
                <form className='form' onSubmit={submit}>
                    <label htmlFor='todo-title'>Title</label>
                    <input
                        id='todo-title'
                        type='text'
                        onChange={e => setTitle(e.target.value)}
                    />

                    <label htmlFor='todo-youtubeurl'>YouTube URL</label>
                    <input
                        id='todo-youtubeurl'
                        type='url'
                        onChange={e => setYouTubeUrl(e.target.value)}
                    />

                    <label htmlFor='todo-description'>Description</label>
                    <textarea
                        id='todo-description'
                        name='todo-description'
                        onChange={e => setDescription(e.target.value)}
                    />
                    {/* <div className='buttons-div'> */}
                    <input type='submit' value='Submit' />

                    {/* </div> */}
                </form>
                <Link to='/'>
                    <button className='type-button' value='Home'>Home</button>
                </Link>
            </div>
        </div>
    )
}
