import React, { useState, useEffect } from 'react'

export default function Form(props) {

    const { title, youTubeUrl, description } = props.props.todoList;

    const [titleUpdate, setTitleUpdate] = useState('');
    // const [youTubeUrl, setYouTubeUrl] = useState();
    // const [description, setDescription] = useState();
    // const [error, setError] = useState();
    // const [todoSuccess, setTodoSuccess] = useState();


    useEffect(() => {
        setTitleUpdate(title);

    })

    const handleInputChange = e => {
        e.preventDefault();
        const { value } = e.target;
        setTitleUpdate(value);
      }
    const submit = async (e) => {
        console.log('in on submit')
        e.preventDefault();
        try {
            // const updateTodo = { title, youTubeUrl, description }

            console.log(titleUpdate);

            //     await API.createTodos(updateTodo, userData.token)
            //         .then(res => setTodoSuccess(`Success, ${userData.user.displayName}!  Your Todo has been saved.`))
            //         // .then API.get
            //         .catch(err =>
            //             (err.response.data.msg && setError(err.response.data.msg))
            //         )

        } catch (err) {
            console.log("something when wrong")
        }
    }

    // console.log('in forms', title, youTubeUrl, description )
    return (
        <>
            {/* {console.log(typeof title)} */}
            <form className='form' onSubmit={submit}>
                <label htmlFor='todo-title'>Title</label>
                <input
                    id='todo-title'
                    type='text'
                    value={titleUpdate}
                    onChange={e => handleInputChange(e)}
                ></input>

                <label htmlFor='todo-youtubeurl'>YouTube Desktop Application URL</label>
                <input
                    id='todo-youtubeurl'
                    type='url'
                    value={youTubeUrl}
                // onChange={e => props.setYouTubeUrl(e.target.value)}
                />

                <label htmlFor='todo-description'>Description</label>
                <textarea
                    id='todo-description'
                    name='todo-description'
                    value={description}
                // onChange={e => props.setDescription(e.target.value)} 
                />
                <div className='buttons-div'>
                    <input type='submit' value='Submit' />
                    <input type='reset' value='Clear' />
                </div>
            </form>
        </>
    )
}
